/**
 * Add classes to the row.
 *
 * @since 1.0.0
 * @param array $classes An array of CSS classes.
 * @param stdClass $data The row data.
 * @return array
 */
function brix_section_row_classes( $classes, $data ) {
	if ( isset( $data->_fluid ) && $data->_fluid == '1' ) {
		$classes[] = 'brix-section-column-height-fluid';
	}

	return $classes;
}

add_filter( 'brix_section_row_classes', 'brix_section_row_classes', 10, 2 );

/**
 * Display a list of row layouts to choose from.
 *
 * @since 1.2
 */
function brix_section_row_layout_lists() {
	?>
	<ul class="brix-section-row-layout-list brix-section-row-layout-default-list">
		<?php foreach ( brix_row_layouts( array( 'default' => true ) ) as $layout ) : ?>
			<li>
				<a href="#" data-title="<?php echo esc_attr( $layout['label'] ); ?>" class="<?php echo esc_attr( $layout['class'] ); ?>" data-layout="<?php echo esc_attr( $layout['layout'] ); ?>">
					<span><?php echo esc_html( $layout['label'] ); ?></span>
					<?php echo $layout['icon']; ?>
				</a>
			</li>
		<?php endforeach; ?>
	</ul>

	<ul class="brix-section-row-layout-list brix-section-row-layout-advanced-list">
		<?php foreach ( brix_row_layouts( array( 'default' => false ) ) as $layout ) : ?>
			<li>
				<a href="#" data-title="<?php echo esc_attr( $layout['label'] ); ?>" class="<?php echo esc_attr( $layout['class'] ); ?>" data-layout="<?php echo esc_attr( $layout['layout'] ); ?>">
					<span><?php echo esc_html( $layout['label'] ); ?></span>
					<?php echo $layout['icon']; ?>
				</a>
			</li>
		<?php endforeach; ?>
	</ul>
	<?php
}

/**
 * Display a list of row layouts to choose from.
 *
 * @since 1.2
 */
function brix_section_row_layout_choices() {
	?>
	<div class="brix-section-row-layout-choices">
		<span class="brix-section-row-layout-label brix-section-row-layout-pre-selector-label"><span class="brix-section-row-editing-label"><?php echo esc_html( __( 'Row layout.', 'brix' ) ); ?></span> <span class="brix-section-row-layout-more">(<?php echo esc_html( __( 'Show all', 'brix' ) ); ?>)</span></span>

		<?php brix_section_row_layout_lists(); ?>
	</div>
	<?php
}